mapper.sql:
SELECT cart_id, customer_id, product_code, product_count, checked
FROM cart

  customer_id = #{customer_id}

UPDATE cart
SET product_count = #{product_count}
WHERE cart_id = #{cart_id}

UPDATE cart
SET checked = #{checked}
WHERE cart_id = #{cart_id}

SELECT cart_id, customer_id, product_code, product_count, checked
FROM cart
WHERE cart_id = #{cart_id}
